import Vue from 'vue'
import Vuex from 'vuex'
import axios from 'axios'
//import axios from 'axios'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    status: '',
    token: localStorage.getItem('token') || '',
    user: null
  },

  // MUTATIONS - Change the state of a vuex store
  mutations: {

    SET_USER_DATA(state, userData) {
      state.user = userData
      localStorage.setItem('user', JSON.stringify(userData))
      axios.defaults.headers.common['Authorization'] = `Bearer ${ userData.token }`
    },
    auth_request(state) {
      state.status = 'loading'
    },
    auth_success(state) {
      state.status = 'success'
      state.token = 'token'
      state.user = 'user'
    },
    auth_error(state) {
      state.status = 'error'
    },
    logout(state) {
      state.status = ''
      state.token = ''
    }
  },

  // ACTIONS
  actions: {
    register({ commit }, credentials) {
      return axios.post('http://0.0.0.0:3000/user', credentials).then(
        ({ data }) => {
          commit('SET_USER_DATA', data)
        }
      )
    },
    login({ commit }, user) {
      return new Promise((resolve, reject) => {
        commit('auth_request');
        axios({url: 'http://0.0.0.0:3000/authenticate', crossdomain: true, data: user, method: 'POST'})
        .then(response => {

          console.log(response);
          const token = response.data.token;
          const user = response.data.user;
          localStorage.setItem('token', token);
          axios.defaults.headers.common['Authorization'] = token;
          commit('auth_success', token, user);
          resolve(response);
        })
        .catch(error => {
          commit('auth_error');
          localStorage.removeItem('token');
          reject(error);
        })
      })
    },

    logout({commit}) {
      return new Promise((resolve) => {
        commit('logout');
        localStorage.removeItem('token');
        delete axios.defaults.headers.common['Authorization'];
        resolve();
      })
    }
  },

  // Getters - Get the value of the vuex state
  getters: {
    isLoggedIn: state => !!state.token,
    authStatus: state => state.status,
  },
  modules: {

  }
})
